# This implements a phase vocoder for time-stretching/compression# Put the file name of the input file in "infile"# the file to be created is "outfile"# The amount of time-stretch is determined by the ratio of the hopin# and hopout variables. For example, hopin=242 and hopout=161.3333# (integers are not required) increases the tempo by # hopin/hopout = 1.5. To slow down a comparable amount,# choose hopin = 161.3333, hopout = 242.from __future__ import divisionimport mathimport numpyimport thinkdspfrom find_peaks import find_peaksdef phase_vocoder(input_wave, hop_in=121, hop_out=242, max_peak=20):    input_wave.normalize()    all_2_pi = 2 * math.pi * numpy.arange(0, 101)  # all multiples of 2 pi (used in PV-style freq search)    eps_peak = 0.005  # height of peaks    segment_length = 2 ** 12    fft_length = segment_length / 2  # fft length (divide by 2 to eliminate redundant negative frequencies)    window = numpy.hanning(segment_length)    tt = numpy.zeros(numpy.ceil(hop_out / hop_in) * len(input_wave))  # place for output    number_of_segments = int(numpy.floor((len(input_wave) - segment_length) / hop_in))    ssf = input_wave.framerate * numpy.arange(0, fft_length + 1) / segment_length  # frequency vector    phold = numpy.zeros(fft_length + 1)    phadvance = numpy.zeros(fft_length + 1)    outbeat = numpy.zeros(segment_length)    dt_in = hop_in / input_wave.framerate  # time advances dt per hop for input    dt_out = hop_out / input_wave.framerate  # time advances dt per hop for output    for segment_number in range(number_of_segments):  # main loop - process each segment        index_in = numpy.round(numpy.arange(segment_number * hop_in, segment_number * hop_in + segment_length))        s = window * input_wave.ys[index_in]  # get this frame and take FFT        ffts = numpy.fft.fft(s)        spectrum = numpy.abs(ffts[:fft_length+1])        ph = numpy.angle(ffts[:fft_length+1])        # find peaks to define spectral mapping        peak_positions = find_peaks(spectrum, max_peak, eps_peak)        peak_values = spectrum[peak_positions[:, 1]]        inds = numpy.argsort(peak_values)        peaksort = peak_positions[inds, :]        pc = peaksort[:, 1]        # estimate frequency using PV strategy        best_frequency = numpy.zeros(pc.shape)        for tk, peak_index in enumerate(pc):            dtheta = ph[peak_index] - phold[peak_index] + all_2_pi            frequency_estimates = dtheta / (2 * math.pi * dt_in)            indf = numpy.argmin(numpy.abs(ssf[peak_index] - frequency_estimates))            best_frequency[tk] = frequency_estimates[indf]  # find best freq estimate for each row        # generate output spectrum and phase        spectrum_out = spectrum        phout = ph        for tk in range(len(pc)):            fdes = best_frequency[tk]  # reconstruct with original frequency            freqind = numpy.arange(peaksort[tk, 0], peaksort[tk, 2])  # indices of the surrounding bins            # specify magnitude and phase of each partial            spectrum_out[freqind] = spectrum[freqind]            phadvance[peaksort[tk, 1]] += 2 * math.pi * fdes * dt_out            pizero = math.pi * numpy.ones(len(freqind))            pcent = peaksort[tk, 1] - peaksort[tk, 0] + 1            indpc = numpy.arange((1 - numpy.mod(pcent, 2)), len(freqind), 2)            pizero[indpc] = numpy.zeros([1, len(indpc)])            phout[freqind] = phadvance[peaksort[tk, 1]] + pizero        # reconstruct time signal (stretched or compressed)        compl = spectrum_out * numpy.exp(1j * phout)        compl[fft_length] = ffts[fft_length]        compl = numpy.concatenate([compl, numpy.conj(compl[1:fft_length])[::-1]])        wave = numpy.real(numpy.fft.ifft(compl))        outbeat[:] = wave        phold[:] = ph        indout = numpy.round(numpy.arange(segment_number * hop_out, segment_number * hop_out + segment_length)).astype(int)        tt[indout] = tt[indout] + outbeat    output_wave = thinkdsp.Wave(tt, input_wave.framerate)    output_wave.normalize()    return output_waveif __name__ == "__main__":    input_file = "violin.wav"    output_file = "yoursoundchanged.wav"    input_wave = thinkdsp.read_wave(input_file)    output_wave = phase_vocoder(input_wave)    output_wave.write(output_file)